package Day1;


/*
 * Primitive data type-> built in data type-->int(byte->1byte,short->2byte,int->4byte,long),float,char->2bytes,boolean->1bit
 * 1 byte->8 bits
 * Non-primitive data type-> user defined data type--> class,object,interface,string
 * 
 * 
 */

public class DataType_char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='a';
		System.out.println(ch);
		
		//assigning num to char
		char ch1=50;
		System.out.println(ch1);
		
		//assigning unicode to char
		char var1='\u0041';
		System.out.println(var1);
		
		//unicode representation
		int a='A';
		System.out.println(a);

	}

}

