
public class ForEachloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[]= {34,21,79,43};
		for(int i:a)
		{
			System.out.println(i);
		}
		
		char ch[]= {'A','b','e'};
		for(char c:ch)
		{
			System.out.println(c);
		}

		String s[]= {"Java","C","C++"};
		for(String str:s)
		{
			System.out.println(str);
		}
		
	}

}
